package day3;

public class JumpingStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i=10;
		
		for(i=0;i<=10;i++) {
			if(i==5) {
				continue; //when i =5 it will just skip this
				
			}
			
			System.out.println(i);
			
		}
	
	}

}
